mapper.delete:

delete from SPAWNING_SITE_AND_FISH
where ID = #{id,jdbcType=VARCHAR}
  and SPAWNING_ID = #{spawningId,jdbcType=VARCHAR}
  and FISH_ID = #{fishId,jdbcType=VARCHAR}

mapper.update:

update SPAWNING_SITE_AND_FISH
set SPAWN_RUNOFF = #{spawnRunoff,jdbcType=NUMERIC},
  SPAWN_COUNT = #{spawnCount,jdbcType=NUMERIC}
where ID = #{id,jdbcType=VARCHAR}
  and SPAWNING_ID = #{spawningId,jdbcType=VARCHAR}
  and FISH_ID = #{fishId,jdbcType=VARCHAR}

mapper.select:

select ID, SPAWNING_ID, FISH_ID, SPAWN_RUNOFF, SPAWN_COUNT
from SPAWNING_SITE_AND_FISH
where ID = #{id,jdbcType=VARCHAR}
  and SPAWNING_ID = #{spawningId,jdbcType=VARCHAR}
  and FISH_ID = #{fishId,jdbcType=VARCHAR}
